{-# LANGUAGE BlockArguments #-}
{-# LANGUAGE LambdaCase #-}

module Euler_8 where

import GHC.Natural ( naturalFromInteger, Natural, naturalToInt )
import Data.Functor.Foldable
    ( ListF(Cons, Nil), Recursive(para, cata) )

number :: Natural
number = 

numberButList :: Natural -> [Natural]
numberButList = cata f . show where
    f Nil = []
    f (Cons x a) = (read [x] :: Natural) : a

-- | >>> biggestNMul 13 number
-- 23514624000
biggestNMul :: Int -> Natural -> Natural
biggestNMul amount num = para f $ naturalFromInteger 1000 where
    f Nothing = product $ take amount $ numberButList num
    f (Just (x, a)) = if n > a then n else a where
        n = product $ take amount $ drop (naturalToInt x) $ numberButList num
